getPSAppViewLogics.scriptCode:
console.log("控制评论容器显隐"+view);
const form = view.getController('form');
if (form) {
    form.evt.on('onFormDetailEvent', event =>{
        const panelItems = view.layoutPanel.panelItems;
        panelItems.send_comment_container.state.visible = false;
        if (!panelItems.comment_container) {
            return;
        }
        if (form.details.tabpanel1) {
            const activeTab = form.details.tabpanel1.state.activeTab;
            panelItems.comment_container.state.visible = activeTab === 'detailpage';
        }
    });
}

console.log('附件上传', view)
if(view.layoutPanel.panelItems.mdctrl.control.state.items.length == 0){
	view.layoutPanel.panelItems.mdctrl.state.keepAlive = true;
    view.layoutPanel.panelItems.mdctrl.state.visible = false;
} else {
    view.layoutPanel.panelItems.mdctrl.state.visible = true;
}
