const modeConfigs = {
  precise: { temperature: 0.1, top_p: 0.7 },
  balanced: { temperature: 1, top_p: 0.7 },
  creative: { temperature: 1, top_p: 0.8 }
};

const config = modeConfigs[data.generation_mode];
if (config) {
  data.temperature = config.temperature;
  data.top_p = config.top_p;
}
