getPSDEGridDataItems.scriptCode:
const app = ibiz.hub.getApp(context.srfappid);
const tempparams = {...params, work_item_type_id: data.work_item.work_item_type_id, project_id: data.work_item.project_id, state: data.work_item.state}
const dataItems = await app.codeList.get(
    'plmweb.projmgmt__scrum_state',
    context,
    tempparams,
);
const value = data.work_item.state;
const item =dataItems.find(x => x.value === value);
if (item) {
    return `<div class="ibiz-dropdown ibiz-dropdown--readonly ibiz-dropdown--round ibiz-grid-field-edit-column__editor" title="${item.text}"><span class="ibiz-dropdown-readonly-text-item" style="--ibiz-dropdown-readonly-text-item-color: ${item.color}; --ibiz-dropdown-select-option-item-color: ${item.color}; --ibiz-dropdown-select-option-item-bkcolor: ${item.bkcolor};"><span class="ibiz-dropdown-readonly-text-item__label">${item.text}</span></span></div>`;
}

getPSAppViewLogics.scriptCode:
view.layoutPanel.panelItems.choose_data.state.visible = view.context.srfshowchoose || false;

// 初始化默认隐藏表格
view.layoutPanel.panelItems.grid.state.keepAlive = true;
view.layoutPanel.panelItems.grid.state.visible = false;
const form = view.getController('form');
if (form) {
    form.evt.on('onFormDetailEvent', event =>{
        const panelItems = view.layoutPanel.panelItems;
        if (!panelItems.comment) {
            return;
        }
        if (event.formDetailName === 'tabpage1') {
            panelItems.comment.state.visible = true;
        } else {
            panelItems.comment.state.visible = false;
        }
    });
}

// 初始化隐藏发送和清空按钮
