ctrl.state.data.work_item_type_name = null;

// 调用表单项更新设置缺陷类型
const params = { ...ctrl.params, ...ctrl.data.getOrigin() };

const res = await ctrl.service.updateFormItem(
    'set_type_bug',
    ctrl.context,
    params,
);

return res.data

getPSControlAttributes.attrValue:
(time) => {
    if (!data.end_at) {
        return false;
    }
    const end_at = new Date(data.end_at);
    // 比对天
    time.setHours(0, 0, 0, 0);
    end_at.setHours(0, 0, 0, 0);
    return time.getTime() > end_at.getTime();
}

(time) => {
    if (!data.start_at) {
        return false;
    }
    const start_at = new Date(data.start_at);
    // 比对天
    start_at.setHours(0, 0, 0, 0);
    time.setHours(0, 0, 0, 0);
    return time.getTime() < start_at.getTime();
}

