getPSDEFormItemUpdates.scriptCode:
var form_data = view.layoutPanel.panelItems.form.control.data;
var actual_workload = form_data.actual_workload;
var estimated_workload = form_data.estimated_workload; 
var remaining_workload = form_data.remaining_workload;  
var estimated = 0; // 预估工时
if(estimated_workload){
	estimated = Number(estimated_workload);
}
var actual = 0; // 已登记的实际工时
if(actual_workload){
	actual = Number(actual_workload);
}
var remaining = 0;
if(remaining_workload){
	remaining = Number(remaining_workload);
}
// 计算工时进度
if((actual + remaining) != 0){
	var schedule = ((actual / (actual + remaining)) * 100).toFixed(1);
	form_data.workload_schedule = schedule;
}

console.log('进入计划时间的校验...');
var form_data = view.layoutPanel.panelItems.form.control.data;
var plan_at = form_data.plan_at;
console.log('plan_at:'+plan_at);

//计划时间
var planAtArr=plan_at.split(',').map(dateStr => new Date(dateStr));
if(planAtArr.length > 1){
    var startTime=planAtArr[0];
    var endTime=planAtArr[1];;
    console.log('startTime:'+startTime);
    console.log('endTime:'+endTime);
    if(startTime > endTime){
        form_data.plan_at='';
        util.message.error('计划开始时间不能大于结束时间!');
    }
}

var form_data = view.layoutPanel.panelItems.form.control.data;
var actual_workload = form_data.actual_workload;
var estimated_workload = form_data.estimated_workload;  
var estimated = 0; // 预估工时
if(estimated_workload){
	estimated = Number(estimated_workload);
}
var actual = 0; // 已登记的实际工时
var remaining = 0; // 剩余工时
if(actual_workload){
	actual = Number(actual_workload);
	if(estimated_workload){
		// 计算剩余工时
		remaining = (estimated - actual) >= 0 ? (estimated - actual) : 0;
		form_data.remaining_workload = remaining;
	}
} else {
	remaining = estimated;
	form_data.remaining_workload = estimated_workload;
}
// 计算工时进度
if((actual + remaining) != 0){
	var schedule = ((actual / (actual + remaining)) * 100).toFixed(1);
	form_data.workload_schedule = schedule;
}

console.log('进入实际时间的校验...');
var form_data = view.layoutPanel.panelItems.form.control.data;
var real_at = form_data.real_at;
var realAtArr=real_at.split(',').map(dateStr => new Date(dateStr));
if(realAtArr.length > 1){
    var startTime=realAtArr[0];
    var endTime=realAtArr[1];;
    console.log('startTime:'+startTime);
    console.log('endTime:'+endTime);
    if(startTime > endTime){
        form_data.real_at='';
        util.message.error('实际开始时间不能大于结束时间!');
    }

}

