const result = await ibiz.util.file.chooseFileAndUpload(context, params, data, { multiple: true, showUploadManager: true });
if (result) {
    const items = result.map(item => {
        return {
            uuid: item.id,
            ...item,
        };
    });
    Object.assign(data, items);
}
return { data: result };

