(time) => {
    const searchForm = view.ctx.controllersMap.get('searchform');
    if (!searchForm) {
        return false;
    }

    const formItem = searchForm.formItems.find((item) => item.model.codeName === 'date_range');
    if(!formItem) {
        return false;
    }

    // 获取日期弹框内选择日期的选中值t
    const dateRange = formItem.editor.dateRange;
    if (!dateRange || dateRange && dateRange.length === 0) {
        return false;
    }

    const currentTime = dateRange[0];
    currentTime.setHours(0, 0, 0, 0);
    time.setHours(0, 0, 0, 0);

    const beforeDays = new Date(
        currentTime.getTime() - 31 * 24 * 60 * 60 * 1000,
    );
    const afterDays = new Date(
        currentTime.getTime() + 31 * 24 * 60 * 60 * 1000,
    );

    return (
        beforeDays.getTime() >= time.getTime() ||
        afterDays.getTime() <= time.getTime()
    );
}

