    text: '本周',
    value: () => {
        const end_at = new Date();
        const currentDay = end_at.getDay();
        const start_at = new Date(end_at);

        // 计算本周的开始日期 (周一)
        start_at.setDate(
        end_at.getDate() - currentDay + (currentDay === 0 ? -6 : 1),
        );
        return [start_at, end_at];
    },
    },
    {
    text: '本月',
    value: () => {
        const end_at = new Date();
        const start_at = new Date(end_at);

        // 将日期设置为本月第一天
        start_at.setDate(1);
        return [start_at, end_at];
    },
    },
