getPSAppViewLogics.scriptCode:
view.layoutPanel.panelItems.choose_data.state.visible = view.context.srfshowchoose || false;

// 初始化默认隐藏表格
view.layoutPanel.panelItems.grid.state.keepAlive = true;
view.layoutPanel.panelItems.grid.state.visible = false;
const form = view.getController('form');
if (form) {
    form.evt.on('onFormDetailEvent', event =>{
        const panelItems = view.layoutPanel.panelItems;
        if (!panelItems.comment) {
            return;
        }
        if (event.formDetailName === 'tabpage1') {
            panelItems.comment.state.visible = true;
        } else {
            panelItems.comment.state.visible = false;
        }
    });
}

// 初始化隐藏发送和清空按钮
view.layoutPanel.panelItems.button_calluilogic1.state.visible = false
view.layoutPanel.panelItems.button_calluilogic.state.visible = false

getPSControlRenders.layoutPanelModel:
const target_assignee = data[controller.fieldName] || {};
const userid = target_assignee.assignee_id || '';
const usertext = target_assignee.assignee_name || '';
const app = await ibiz.hub.getApp(context.srfappid);
const dataItems = await app.codeList.get(
    'SysOperator',
    context,
    params,
)
if (!dataItems || dataItems.length === 0) {
    return '';
}
let avatarContent = ibiz.util.text.abbreviation(usertext) || '';
let avatarBg = ibiz.util.text.stringToHexColor(usertext);
const operator = dataItems.find((item) => (item.value && userid && item.value === userid) || (item.text && usertext && item.text === usertext)) || {};
if (operator.data && operator.data.iconurl) {
    const urlConfig = JSON.parse(operator.data.iconurl);
    if (urlConfig.length > 0) {
        const { downloadUrl } = ibiz.util.file.calcFileUpDownUrl(
            context,
            params,
            data,
            controller.editor ? controller.editor.editorParams : {},
        );
        const url = downloadUrl.replace('%fileId%', urlConfig[0].id);
        avatarContent = `<img style="width:100%; height:100%; object-fit:cover; border-radius:50%;"
                src="${url}" alt="">`;
    }
}
return `
<div
    style="overflow:hidden; width:max-content; display:flex; gap: 8px; align-items:center; height:var(--ibiz-height-control-default);"
    title=""
>
    <div 
        style="width:var(--ibiz-height-control-default); min-width:var(--ibiz-height-control-default); height:var(--ibiz-height-control-default); overflow:hidden; font-size:12px; line-height:var(--ibiz-height-control-default); color:var(--ibiz-color-primary-active-text); text-align:center; border-radius:50%;background-color: ${avatarBg};"
    >
    ${avatarContent}
    </div>
    <div
        style="max-width: 90px;display:flex; align-items:center; justify-content:left; height:var(--ibiz-height-control-default); overflow:hidden; text-overflow:ellipsis; white-space:nowrap; overflow:hidden; text-overflow:ellipsis; white-space:nowrap;"
    >
        <div>${usertext}</div>
    </div>
</div>
`;
